<script>
  import Range from "./Range";

  const defaultOptions = {
    taxRate: 30,
    governmentRate: 0.67,
    deposits: 0,
    quarters: [
      {
        title: 'Kvartal 1',
        value: 0,
      },
      {
        title: 'Kvartal 2',
        value: 0,
      },
      {
        title: 'Kvartal 3',
        value: 0,
      },
      {
        title: 'Kvartal 4',
        value: 0,
      }
    ]
  };

  export default {
    components: {Range},
    data() {
      return {
        lsKey: 'iskOptions',
        defaultOptions: defaultOptions,
        options: defaultOptions,
      }
    },

    created() {
      const storedOptions = window.localStorage.getItem(this.lsKey);
      if (storedOptions) {
        this.options = JSON.parse(storedOptions)
      }
    },

    watch: {
      options: {
        deep: true,
        handler(val) {
          this.saveOptions(val)
        }
      },
    },

    methods: {
      saveOptions(options) {
        window.localStorage.setItem(this.lsKey, JSON.stringify(options))
      },
      resetOptions() {
        if (!confirm('Är du säker på att du vill ta bort dina egna inställningar och börja om från början?')) {
          return
        }

        window.localStorage.removeItem('options')
        this.options = this.defaultOptions
      },
      formatMoney(val) {
        return new Intl.NumberFormat('sv-SE', {
          style: 'decimal',
          maximumFractionDigits: 0
        }).format(val) + ' kr'
      },
    },

    computed: {
      averageValue() {
        const q = this.options.quarters;
        const combinedQuartersValue = q[0].value + q[1].value + q[2].value + q[3].value;

        return (combinedQuartersValue + this.options.deposits) / 4
      },
      taxToPay() {
        return (this.averageValue * ((this.options.governmentRate + 1) / 100)) * 0.3
      }
    }
  }
</script>

<template>
    <div class="custom-bg-pattern text-gray-700 bg-gray-100">
        <div class="max-w-4xl ml-auto mr-auto overflow-auto p-4">
            <h1 class="text-5xl font-medium text-center text-gray-900 mt-8 mb-2">Räkna ut ISK skatt</h1>
            <p class="text-xl font-medium text-center mb-8">
                Lorem ipsum dolor sit amet
            </p>

            <div class="relative bg-white border-gray-300 border shadow-lg rounded-lg">
                <div class="grid md:grid-cols-2 gap-8 p-8">
                    <Range
                            label="Stadslåneräntan"
                            v-model.number="options.governmentRate"
                            max="3"
                            min="-0.25"
                            value-suffix=" %"
                            step="0.01"
                    />

                    <Range
                            label="Inättningar"
                            v-model.number="options.deposits"
                            max="1000000"
                            value-suffix=" kr"
                            step="5000"
                    />

                    <Range
                            v-for="quarter in options.quarters"
                            :key="quarter.title"
                            :label="quarter.title"
                            v-model.number="quarter.value"
                            max="50000000"
                            value-suffix=" kr"
                            step="10000"
                    />
                </div>

                <div class="text-center m-2">
                    <span @click="resetOptions" title="Återställ inställningarna" class="font-medium text-center text-blue-300 text-center text-sm w-full cursor-pointer hover:text-blue-500">
                        Återställ
                    </span>
                </div>
            </div>

            <div class="capital-needed my-16 text-center">
                <h1 class="text-2xl font-normal leading-tight">
                    Du behöver betala <span class="text-3xl font-black">{{ formatMoney(taxToPay) }}</span>
                    i skatt på ett värde av
                    <span class="text-3xl font-black">{{ formatMoney(averageValue) }}</span>.
                </h1>
            </div>
        </div>
    </div>
</template>

